const fs = require('fs');
const path = require('path');

// ConfiguraciÃ³n base (misma ruta que Write Tool)
const baseDir = '/home/node/.n8n/research-reports/';
const now = new Date();

// Formatear timestamp actual
const timestampConsulta = now.toLocaleString('es-ES', { 
  timeZone: 'America/La_Paz',
  year: 'numeric', 
  month: '2-digit', 
  day: '2-digit',
  hour: '2-digit', 
  minute: '2-digit', 
  second: '2-digit'
});

try {
  // Verificar si el directorio existe
  if (!fs.existsSync(baseDir)) {
    // Intentar crear el directorio
    try {
      fs.mkdirSync(baseDir, { recursive: true });
    } catch (createError) {
      return `âŒ **Error: Directorio no encontrado y no se pudo crear**\n\nğŸ” **Problema:**\n   â””â”€ Directorio: ${baseDir}\n   â””â”€ Error: ${createError.message}\n\nğŸ’¡ **SoluciÃ³n manual:**\n   â””â”€ Crear directorio: \`docker exec n8n-local mkdir -p ${baseDir}\`\n   â””â”€ Verificar permisos: \`docker exec n8n-local ls -la ${path.dirname(baseDir)}\`\n\nğŸ“‹ **RecomendaciÃ³n:** Ejecutar Write Tool primero para crear la estructura`;
    }
  }

  // Leer archivos del directorio
  const files = fs.readdirSync(baseDir);

  // Si no hay archivos
  if (files.length === 0) {
    return `ğŸ“‚ **DIRECTORIO RESEARCH-REPORTS**\n\nğŸ“Š **ESTADO:**\n   â””â”€ Directorio: ${baseDir}\n   â””â”€ Estado: âœ… Existe y accesible\n   â””â”€ Contenido: ğŸ“­ VacÃ­o (0 archivos)\n   â””â”€ Consultado: ${timestampConsulta}\n\nğŸ’¡ **Sugerencia:**\n   â””â”€ Crear archivo de prueba: "escribir hola mundo"\n   â””â”€ Verificar directorio: \`docker exec n8n-local ls -la ${baseDir}\`\n\nâœ… **ESTADO FINAL:** Directorio listo para recibir archivos`;
  }

  // Procesar informaciÃ³n de cada archivo
  const filesInfo = [];
  let totalSize = 0;
  let holaWorldCount = 0;
  let latestFile = null;
  let latestTime = 0;

  files.forEach(filename => {
    const filepath = path.join(baseDir, filename);

    try {
      const stats = fs.statSync(filepath);

      // Solo procesar archivos (no directorios)
      if (stats.isFile()) {
        const sizeKB = (stats.size / 1024).toFixed(2);
        const modifiedTime = stats.mtime;
        const formattedDate = modifiedTime.toLocaleString('es-ES', {
          timeZone: 'America/La_Paz',
          year: 'numeric',
          month: '2-digit', 
          day: '2-digit',
          hour: '2-digit',
          minute: '2-digit'
        });

        const isHolaMundo = filename.startsWith('hola_mundo_');
        if (isHolaMundo) holaWorldCount++;

        // Encontrar archivo mÃ¡s reciente
        if (modifiedTime.getTime() > latestTime) {
          latestTime = modifiedTime.getTime();
          latestFile = {
            name: filename,
            size: sizeKB,
            date: formattedDate,
            isHolaMundo: isHolaMundo
          };
        }

        filesInfo.push({
          name: filename,
          size_kb: sizeKB,
          size_bytes: stats.size,
          modified: formattedDate,
          extension: path.extname(filename),
          is_hola_mundo: isHolaMundo,
          modified_timestamp: modifiedTime.getTime()
        });

        totalSize += stats.size;
      }
    } catch (fileError) {
      // Si hay error con un archivo especÃ­fico, continuar con los demÃ¡s
      console.log(`Error procesando archivo ${filename}: ${fileError.message}`);
    }
  });

  // Ordenar archivos por fecha de modificaciÃ³n (mÃ¡s reciente primero)
  filesInfo.sort((a, b) => b.modified_timestamp - a.modified_timestamp);

  // Calcular estadÃ­sticas
  const totalSizeKB = (totalSize / 1024).toFixed(2);
  const otherFilesCount = filesInfo.length - holaWorldCount;

  // Construir respuesta formateada
  let response = `ğŸ“‚ **ARCHIVOS EN RESEARCH-REPORTS**\n\n`;

  // EstadÃ­sticas generales
  response += `ğŸ“Š **ESTADÃSTICAS:**\n`;
  response += `   â””â”€ Total archivos: ${filesInfo.length}\n`;
  response += `   â””â”€ Archivos "hola_mundo": ${holaWorldCount}\n`;
  response += `   â””â”€ Otros archivos: ${otherFilesCount}\n`;
  response += `   â””â”€ Espacio total: ${totalSizeKB} KB\n`;
  response += `   â””â”€ Consultado: ${timestampConsulta}\n\n`;

  // Lista de archivos
  response += `ğŸ“‹ **LISTA DETALLADA:**\n`;

  filesInfo.forEach((file, index) => {
    const icon = file.is_hola_mundo ? 'ğŸ§ª' : 'ğŸ“„';
    const isLatest = latestFile && file.name === latestFile.name ? ' â­ MÃS RECIENTE' : '';

    response += `   ${index + 1}. ${icon} ${file.name}${isLatest}\n`;
    response += `      â””â”€ ${file.size_kb} KB | ${file.modified}\n`;
  });

  // Archivo mÃ¡s reciente destacado
  if (latestFile) {
    const latestIcon = latestFile.isHolaMundo ? 'ğŸ§ª' : 'ğŸ“„';
    response += `\nğŸ• **ARCHIVO MÃS RECIENTE:**\n`;
    response += `   â””â”€ ${latestIcon} ${latestFile.name}\n`;
    response += `   â””â”€ ${latestFile.size} KB | ${latestFile.date}\n`;
  }

  // Comandos Ãºtiles
  response += `\nğŸ” **COMANDOS ÃšTILES:**\n`;
  response += `   â””â”€ Ver directorio: \`docker exec n8n-local ls -la ${baseDir}\`\n`;
  response += `   â””â”€ Contar archivos: \`docker exec n8n-local find ${baseDir} -name "*.txt" | wc -l\`\n`;
  if (latestFile) {
    response += `   â””â”€ Ver Ãºltimo archivo: \`docker exec n8n-local cat ${baseDir}${latestFile.name}\`\n`;
  }

  response += `\nâœ… **ESTADO:** Directorio verificado y accesible`;

  return response;

} catch (error) {
  // Manejo de errores
  return `âŒ **Error al listar archivos**\n\nğŸ” **Detalles del error:**\n   â””â”€ Tipo: ${error.code || 'Error desconocido'}\n   â””â”€ Mensaje: ${error.message}\n   â””â”€ Directorio: ${baseDir}\n\nğŸ’¡ **Posibles soluciones:**\n   â””â”€ Verificar existencia: \`docker exec n8n-local ls -la ${path.dirname(baseDir)}\`\n   â””â”€ Crear directorio: \`docker exec n8n-local mkdir -p ${baseDir}\`\n   â””â”€ Verificar permisos: \`docker exec n8n-local chmod 755 ${baseDir}\`\n\nğŸ› ï¸ **DiagnÃ³stico:**\n   â””â”€ Error de permisos: ${error.code === 'EACCES' ? 'âŒ Sin acceso' : 'â“ Verificar'}\n   â””â”€ Directorio no existe: ${error.code === 'ENOENT' ? 'âŒ No encontrado' : 'â“ Verificar'}\n\nâš ï¸ **RecomendaciÃ³n:** Ejecutar Write Tool primero para crear estructura de directorio`;
}
